mapper.select:
SELECT


,huodong.huodong_uuid_number as huodongUuidNumber
,huodong.huodong_name as huodongName
,huodong.huodong_photo as huodongPhoto
,huodong.huodong_types as huodongTypes
,huodong.huodong_clicknum as huodongClicknum
,huodong.zan_number as zanNumber
,huodong.cai_number as caiNumber
,huodong.huodong_content as huodongContent
,huodong.huodong_delete as huodongDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.yonghu_delete as yonghuDelete

FROM huodong_liuyan  a
left JOIN huodong huodong ON a.huodong_id = huodong.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.huodong_id = #{params.huodongId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.huodong_liuyan_text like CONCAT('%',#{params.huodongLiuyanText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.reply_text like CONCAT('%',#{params.replyText},'%')


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




        and a.huodong_id IS NOT NULL


        and huodong.huodong_uuid_number like CONCAT('%',#{params.huodongUuidNumber},'%')


        and huodong.huodong_name like CONCAT('%',#{params.huodongName},'%')


        and huodong.huodong_types = #{params.huodongTypes}



          and huodong.huodong_clicknum >= #{params.huodongClicknumStart}   


          and huodong.huodong_clicknum <= #{params.huodongClicknumEnd}   


        and huodong.huodong_clicknum = #{params.huodongClicknum}


          and huodong.zan_number >= #{params.zanNumberStart}   


          and huodong.zan_number <= #{params.zanNumberEnd}   


        and huodong.zan_number = #{params.zanNumber}


          and huodong.cai_number >= #{params.caiNumberStart}   


          and huodong.cai_number <= #{params.caiNumberEnd}   


        and huodong.cai_number = #{params.caiNumber}


        and huodong.huodong_content like CONCAT('%',#{params.huodongContent},'%')


          and huodong.huodong_delete >= #{params.huodongDeleteStart}   


          and huodong.huodong_delete <= #{params.huodongDeleteEnd}   


        and huodong.huodong_delete = #{params.huodongDelete}


          and UNIX_TIMESTAMP(huodong.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(huodong.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


order by a.${params.sort} ${params.order}
